import React from "react";
import { data } from "../../data";
import Footer from "../Footer";

const QuestionBlock = () => {
  const handleChange = (event) => {
    console.log(event.target);
  };
  return (
    <div>
      <div>
        {data.questions.map((data, key) => {
          return (
            <div key={key}>
              {data.question}
              {data.questionoption.map((question, key) => {
                return (
                  <div key={key} onChange={handleChange} >
                    <input type={data.questiontype} value={question.optionvalue} name={data.question}/>
                    <label>{question.optionvalue}</label>
                  </div>
                );
              })}
            </div>
          );
        })}
      </div>
      <Footer />
    </div>
  );
};

export default QuestionBlock;
